<?phpclass ControllerExtensionModuleFeaturedBrands extends Controller {		private $error = array();		public function index() {				$this->load->language('extension/module/featured_brands');		$this->document->setTitle($this->language->get('heading_title'));		$this->load->model('setting/module');		$this->load->model('catalog/manufacturer');		if (($this->request->server['REQUEST_METHOD'] == 'POST') && $this->validate()) {						if (!isset($this->request->get['module_id'])) {				$this->model_setting_module->addModule('featured_brands', $this->request->post);			} else {				$this->model_setting_module->editModule($this->request->get['module_id'], $this->request->post);			}			$this->session->data['success'] = $this->language->get('text_success');			$this->response->redirect($this->url->link('marketplace/extension', 'user_token=' . $this->session->data['user_token'] . '&type=module', true));		}		$data['heading_title'] = $this->language->get('heading_title');		$data['text_edit'] = $this->language->get('text_edit');		$data['text_enabled'] = $this->language->get('text_enabled');		$data['text_disabled'] = $this->language->get('text_disabled');		$data['entry_name'] = $this->language->get('entry_name');		$data['entry_title'] = $this->language->get('entry_title');		$data['entry_featured_brands'] = $this->language->get('entry_featured_brands');		$data['entry_status'] = $this->language->get('entry_status');		$data['button_save'] = $this->language->get('button_save');		$data['button_cancel'] = $this->language->get('button_cancel');		if (isset($this->error['warning'])) {			$data['error_warning'] = $this->error['warning'];		} else {			$data['error_warning'] = '';		}		if (isset($this->error['name'])) {			$data['error_name'] = $this->error['name'];		} else {			$data['error_name'] = '';		}				if (isset($this->error['featured_brands'])) {			$data['error_featured_brands'] = $this->error['featured_brands'];		} else {			$data['error_featured_brands'] = '';		}		$data['breadcrumbs'] = array();		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_home'),			'href' => $this->url->link('common/dashboard', 'user_token=' . $this->session->data['user_token'], true)		);		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_module'),			'href' => $this->url->link('extension/module', 'user_token=' . $this->session->data['user_token'], true)		);		if (!isset($this->request->get['module_id'])) {			$data['breadcrumbs'][] = array(				'text' => $this->language->get('heading_title'),				'href' => $this->url->link('extension/module/featured_brands', 'user_token=' . $this->session->data['user_token'], true)			);		} else {			$data['breadcrumbs'][] = array(				'text' => $this->language->get('heading_title'),				'href' => $this->url->link('extension/module/featured_brands', 'user_token=' . $this->session->data['user_token'] . '&module_id=' . $this->request->get['module_id'], true)			);		}		if (!isset($this->request->get['module_id'])) {			$data['action'] = $this->url->link('extension/module/featured_brands', 'user_token=' . $this->session->data['user_token'], true);		} else {			$data['action'] = $this->url->link('extension/module/featured_brands', 'user_token=' . $this->session->data['user_token'] . '&module_id=' . $this->request->get['module_id'], true);		}		$data['cancel'] = $this->url->link('extension/module', 'user_token=' . $this->session->data['user_token'], true);				if (isset($this->request->get['module_id']) && ($this->request->server['REQUEST_METHOD'] != 'POST')) {			$module_info = $this->model_setting_module->getModule($this->request->get['module_id']);		}				$data['user_token'] = $this->session->data['user_token'];		if (isset($this->request->post['name'])) {			$data['name'] = $this->request->post['name'];		} elseif (!empty($module_info)) {			$data['name'] = $module_info['name'];		} else {			$data['name'] = '';		}		if (isset($this->request->post['featured_brands'])) {			$data['featured_brands'] = $this->request->post['featured_brands'];		} elseif (!empty($module_info)) {			$data['featured_brands'] = $module_info['featured_brands'];		} else {			$data['featured_brands'] = array();		}				if ($data['featured_brands']) {						$featuredBrands = array();						foreach ($data['featured_brands'] as $featured_brand) {								$result = $this->model_catalog_manufacturer->getManufacturer($featured_brand);								$featuredBrands[] = array(					'brand_id' => $result['manufacturer_id'],					'name' => $result['name']				);			}						$data['featured_brands'] = $featuredBrands;		}				if (isset($this->request->post['title'])) {			$data['title'] = $this->request->post['title'];		} elseif (!empty($module_info)) {			$data['title'] = $module_info['title'];		} else {			$data['title'] = "";		}						if (isset($this->request->post['status'])) {			$data['status'] = $this->request->post['status'];		} elseif (!empty($module_info)) {			$data['status'] = $module_info['status'];		} else {			$data['status'] = '';		}		$data['header'] = $this->load->controller('common/header');		$data['column_left'] = $this->load->controller('common/column_left');		$data['footer'] = $this->load->controller('common/footer');				$this->response->setOutput($this->load->view('extension/module/featured_brands', $data));	}	protected function validate() {				if (!$this->user->hasPermission('modify', 'extension/module/featured_brands')) {			$this->error['warning'] = $this->language->get('error_permission');		}		if ((utf8_strlen($this->request->post['name']) < 3) || (utf8_strlen($this->request->post['name']) > 64)) {			$this->error['name'] = $this->language->get('error_name');		}		return !$this->error;	}		public function autocomplete() {				$json = array();				if (isset($this->request->get['filter_title']) || isset($this->request->get['filter_model'])) {			if (isset($this->request->get['filter_title'])) {				$filter_title = $this->request->get['filter_title'];			} else {				$filter_title = '';			}						if (isset($this->request->get['limit'])) {				$limit = $this->request->get['limit'];			} else {				$limit = 5;			}			$filter_data = array(				'filter_title' => $filter_title,				'start'        => 0,				'status'	   => 1,				'limit'        => $limit			);						$this->load->model('extension/module/featured_brands');			$results = $this->model_extension_module_featured_brands->getManufacturers($filter_data);						foreach ($results as $result) {				$json[] = array(					'brand_id'    => $result['manufacturer_id'],					'name'        => strip_tags(html_entity_decode($result['name'], ENT_QUOTES, 'UTF-8'))				);			}		}		$this->response->addHeader('Content-Type: application/json');		$this->response->setOutput(json_encode($json));	}		public function install(){		$this->load->model('extension/module/featured_brands');		$this->model_extension_module_featured_brands->install();	}	public function uninstall(){		$this->load->model('extension/module/featured_brands');		$this->model_extension_module_featured_brands->uninstall();	}	}