<?phpclass ControllerExtensionModuleNewsletter extends Controller {		private $error = array();		public function index() {				$this->load->language('extension/module/newsletter');		$this->document->setTitle($this->language->get('heading_title'));		$this->load->model('setting/module');		$this->load->model('tool/image');		if (($this->request->server['REQUEST_METHOD'] == 'POST') && $this->validate()) {						if (!isset($this->request->get['module_id'])) {				$this->model_setting_module->addModule('newsletter', $this->request->post);			} else {				$this->model_setting_module->editModule($this->request->get['module_id'], $this->request->post);			}			$this->session->data['success'] = $this->language->get('text_success');			$this->response->redirect($this->url->link('marketplace/extension', 'user_token=' . $this->session->data['user_token'] . '&type=module', true));		}				if (isset($this->error['warning'])) {			$data['error_warning'] = $this->error['warning'];		} else {			$data['error_warning'] = '';		}		if (isset($this->error['name'])) {			$data['error_name'] = $this->error['name'];		} else {			$data['error_name'] = '';		}				if (isset($this->error['newsletter'])) {			$data['error_newsletter'] = $this->error['newsletter'];		} else {			$data['error_newsletter'] = '';		}		$data['breadcrumbs'] = array();		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_home'),			'href' => $this->url->link('common/dashboard', 'user_token=' . $this->session->data['user_token'], true)		);		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_module'),			'href' => $this->url->link('extension/module', 'user_token=' . $this->session->data['user_token'], true)		);		if (!isset($this->request->get['module_id'])) {			$data['breadcrumbs'][] = array(				'text' => $this->language->get('heading_title'),				'href' => $this->url->link('extension/module/newsletter', 'user_token=' . $this->session->data['user_token'], true)			);		} else {			$data['breadcrumbs'][] = array(				'text' => $this->language->get('heading_title'),				'href' => $this->url->link('extension/module/newsletter', 'user_token=' . $this->session->data['user_token'] . '&module_id=' . $this->request->get['module_id'], true)			);		}		if (!isset($this->request->get['module_id'])) {			$data['action'] = $this->url->link('extension/module/newsletter', 'user_token=' . $this->session->data['user_token'], true);		} else {			$data['action'] = $this->url->link('extension/module/newsletter', 'user_token=' . $this->session->data['user_token'] . '&module_id=' . $this->request->get['module_id'], true);		}		$data['cancel'] = $this->url->link('extension/module', 'user_token=' . $this->session->data['user_token'], true);				if (isset($this->request->get['module_id']) && ($this->request->server['REQUEST_METHOD'] != 'POST')) {			$module_info = $this->model_setting_module->getModule($this->request->get['module_id']);		}				$data['user_token'] = $this->session->data['user_token'];		if (isset($this->request->post['name'])) {			$data['name'] = $this->request->post['name'];		} elseif (!empty($module_info)) {			$data['name'] = $module_info['name'];		} else {			$data['name'] = '';		}				if (isset($this->request->post['title'])) {			$data['title'] = $this->request->post['title'];		} elseif (!empty($module_info)) {			$data['title'] = $module_info['title'];		} else {			$data['title'] = '';		}				if (isset($this->request->post['description'])) {			$data['description'] = $this->request->post['description'];		} elseif (!empty($module_info)) {			$data['description'] = $module_info['description'];		} else {			$data['description'] = '';		}				if (isset($this->request->post['button_label'])) {			$data['button_label'] = $this->request->post['button_label'];		} elseif (!empty($module_info)) {			$data['button_label'] = $module_info['button_label'];		} else {			$data['button_label'] = '';		}				if (isset($this->request->post['form_url'])) {			$data['form_url'] = $this->request->post['form_url'];		} elseif (!empty($module_info)) {			$data['form_url'] = $module_info['form_url'];		} else {			$data['form_url'] = '';		}				if (isset($this->request->post['list_id'])) {			$data['list_id'] = $this->request->post['list_id'];		} elseif (!empty($module_info)) {			$data['list_id'] = $module_info['list_id'];		} else {			$data['list_id'] = '';		}				if (isset($this->request->post['image_background'])) {			$data['image_background'] = $this->request->post['image_background'];		} elseif (!empty($module_info)) {			$data['image_background'] = $module_info['image_background'];		} else {			$data['image_background'] = '';		}				if (isset($this->request->post['image_background']) && is_file(DIR_IMAGE . $this->request->post['image_background'])) {			$data['thumb_background'] = $this->model_tool_image->resize($this->request->post['image_background'], 100, 100);		} elseif (!empty($module_info) && is_file(DIR_IMAGE . $module_info['image_background'])) {			$data['thumb_background'] = $this->model_tool_image->resize($module_info['image_background'], 100, 100);		} else {			$data['thumb_background'] = $this->model_tool_image->resize('no_image.png', 100, 100);		}				if (isset($this->request->post['status'])) {			$data['status'] = $this->request->post['status'];		} elseif (!empty($module_info)) {			$data['status'] = $module_info['status'];		} else {			$data['status'] = '';		}		$data['header'] = $this->load->controller('common/header');		$data['column_left'] = $this->load->controller('common/column_left');		$data['footer'] = $this->load->controller('common/footer');				$this->response->setOutput($this->load->view('extension/module/newsletter', $data));	}	protected function validate() {				if (!$this->user->hasPermission('modify', 'extension/module/newsletter')) {			$this->error['warning'] = $this->language->get('error_permission');		}		if ((utf8_strlen($this->request->post['name']) < 3) || (utf8_strlen($this->request->post['name']) > 64)) {			$this->error['name'] = $this->language->get('error_name');		}		return !$this->error;	}		public function install(){		$this->load->model('extension/module/newsletter');		$this->model_extension_module_newsletter->install();	}	public function uninstall(){		$this->load->model('extension/module/newsletter');		$this->model_extension_module_newsletter->uninstall();	}	}